mapper.select:
select EMAIL,MNAME as name,TEL,LEVEL
from SPMS_MEMBS 
where EMAIL=#{email} and PWD=#{password}

mapper.update:
update SPMS_MEMBS set
		MNAME=#{name}, TEL=#{tel}, BLOG=#{blog}, TAG=#{tag}, UPDATE_DATE=now()
		where EMAIL=#{email}

update SPMS_MEMBS set
		 MNAME=#{name},TEL=#{tel},BLOG=#{blog},DET_ADDR=#{detailAddress},TAG=#{tag},LEVEL=#{level}
		 where EMAIL=#{email} and PWD=#{password}

update SPMS_MEMBS set
		PWD=#{newPassword},UPDATE_DATE=now()
		where EMAIL=#{email} and PWD=#{oldPassword}
